import { createConsola } from 'consola';
/**
 * Console logger
 */
export const logger = process.env
    .ROLLDOWN_TEST
    ? createTestingLogger()
    : createConsola({
        formatOptions: {
            date: false,
        },
    });
function createTestingLogger() {
    const types = [
        'silent',
        'fatal',
        'error',
        'warn',
        'log',
        'info',
        'success',
        'fail',
        'ready',
        'start',
        'box',
        'debug',
        'trace',
        'verbose',
    ];
    const ret = Object.create(null);
    for (const type of types) {
        ret[type] = console.log;
    }
    return ret;
}
